--
-- Create procedure "osae_sp_object_add"
--
CREATE DEFINER = 'osae'@'%'
PROCEDURE osae_sp_object_add (IN pname varchar(100), IN palias varchar(100), IN pdescription varchar(200), IN pobjecttype varchar(200), IN paddress varchar(200), IN pcontainer varchar(200), IN pmintrustlevel int, IN penabled tinyint(1), OUT results integer)
BEGIN
  DECLARE vAlias varchar(255) DEFAULT '';
  DECLARE vAliasCount int;
  DECLARE vObjectCount int;
  DECLARE vObjectTypeCount int;
  DECLARE iContainer int;
  DECLARE iObjectID int;
  DECLARE vObjectTypeID int;
  DECLARE vContainerCount int;
  DECLARE vContainerID int DEFAULT NULL;
  SET results = 1;
  #Massage the alias to make sure it is unique, or blank it out.
  IF pname != palias THEN
    IF palias != '' THEN
      SELECT
        COUNT(object_id) INTO vAliasCount
      FROM osae_object
      WHERE object_name = palias
      OR object_alias = palias;
      IF vAliasCount > 0 THEN
        SET vAlias = '';
      ELSE
        SET vAlias = palias;
      END IF;
    END IF;

  END IF;
  SELECT
    COUNT(object_type_id) INTO vObjectTypeCount
  FROM osae_object_type
  WHERE object_type = pobjecttype;
  IF vObjectTypeCount > 0 THEN
    SELECT
      object_type_id,
      container INTO vObjectTypeID, iContainer
    FROM osae_object_type
    WHERE object_type = pobjecttype;
    SELECT
      COUNT(object_id) INTO vContainerCount
    FROM osae_v_object
    WHERE object_name = pcontainer
    AND container = 1;
    IF vContainerCount = 1 THEN
      SELECT
        object_id INTO vContainerID
      FROM osae_v_object
      WHERE object_name = pcontainer
      AND container = 1;
    END IF;
    SELECT
      COUNT(object_id) INTO vObjectCount
    FROM osae_object
    WHERE UPPER(object_name) = UPPER(pname)
    OR UPPER(object_alias) = UPPER(pname)
    OR (UPPER(address) = UPPER(paddress)
    AND address IS NOT NULL
    AND address <> '');
    IF vObjectCount = 0 THEN
      INSERT INTO osae_object (object_name, object_alias, object_description, object_type_id, address, container_id, min_trust_level, enabled)
        VALUES (pname, vAlias, pdescription, vObjectTypeID, paddress, vContainerID, pmintrustlevel, penabled);
    ELSE
      CALL osae_sp_debug_log_add(CONCAT('Object_Add Failed to Add ', pname, ' due to duplicate data.'), 'SYSTEM');
      SET results = 3;
    END IF;
    IF iContainer = 1
      AND vContainerCount = 0 THEN
      SELECT
        object_id INTO iObjectID
      FROM osae_object
      WHERE object_name = pname;
      UPDATE osae_object
      SET container_id = iObjectID
      WHERE object_id = iObjectID;
    END IF;
  ELSE
    SET results = 2;
  END IF;
END
$$

--
-- Create procedure "osae_sp_object_container_check"
--
CREATE DEFINER = 'osae'@'%'
PROCEDURE osae_sp_object_container_check (IN pObject varchar(255), IN pContainer varchar(255))
BEGIN
  DECLARE vObjectName varchar(200);
  DECLARE iMatch int;
  SET vObjectName = pObject;
  #Check to see if the Object is Directly assigned to the container.
  SET iMatch = (SELECT
      COUNT(object_name)
    FROM osae_v_object
    WHERE (UPPER(object_name) = UPPER(pObject)
    OR UPPER(object_alias) = UPPER(pObject))
    AND UPPER(container_name) = UPPER(pContainer));
  IF iMatch = 0 THEN
  label1:
    LOOP
      SET vObjectName = (SELECT
          container_name
        FROM osae_v_object
        WHERE (UPPER(object_name) = UPPER(vObjectName)
        OR UPPER(object_alias) = UPPER(vObjectName))
        AND UPPER(container_name) != UPPER(vObjectName));
      IF vObjectName IS NULL
        OR vObjectName = '' THEN
        LEAVE Label1;
      END IF;
      #SELECT vObjectName;
      SET iMatch = (SELECT
          COUNT(object_name)
        FROM osae_v_object
        WHERE (UPPER(object_name) = UPPER(vObjectName)
        OR UPPER(object_alias) = UPPER(vObjectName))
        AND UPPER(container_name) = UPPER(pContainer));
      IF iMatch = 0 THEN
        ITERATE label1;
      END IF;
      LEAVE label1;
    END LOOP label1;
  END IF;
  SELECT
    iMatch;

END
$$

