--
-- Create procedure "osae_sp_object_event_script_add"
--
CREATE DEFINER = 'osae'@'%'
PROCEDURE osae_sp_object_event_script_add (IN pobject varchar(200), IN pevent varchar(200), IN pscriptid int)
BEGIN
  DECLARE vObjectCount int;
  DECLARE vObjectID int;
  DECLARE vObjectTypeID int;
  DECLARE vEventCount int;
  DECLARE vEventID int;
  DECLARE vScriptSeq int;
  SET vScriptSeq = 0;
  SELECT
    COUNT(object_id) INTO vObjectCount
  FROM osae_object
  WHERE UPPER(object_name) = UPPER(pobject)
  OR UPPER(object_alias) = UPPER(pobject);
  IF vObjectCount > 0 THEN
    SELECT
      object_id,
      object_type_id INTO vObjectID, vObjectTypeID
    FROM osae_object
    WHERE UPPER(object_name) = UPPER(pobject)
    OR UPPER(object_alias) = UPPER(pobject);
    SELECT
      COUNT(event_id) INTO vEventCount
    FROM osae_object_type_event
    WHERE object_type_id = vObjectTypeID
    AND (event_name = pevent
    OR event_label = pevent);
    IF vEventCount = 1 THEN
      SELECT
        event_id INTO vEventID
      FROM osae_object_type_event
      WHERE object_type_id = vObjectTypeID
      AND (event_name = pevent
      OR event_label = pevent);
      SELECT
        COALESCE(script_sequence, 0) INTO vScriptSeq
      FROM osae_object_event_script
      WHERE object_id = vObjectID
      AND event_id = vEventID
      ORDER BY script_sequence DESC LIMIT 1;
      INSERT INTO osae_object_event_script (object_id, event_id, script_id, script_sequence)
        VALUES (vObjectID, vEventID, pscriptid, vScriptSeq + 1);
    END IF;
  END IF;
END
$$

