--
-- Create procedure "osae_sp_object_property_set"
--
CREATE DEFINER = 'osae'@'%'
PROCEDURE osae_sp_object_property_set (IN pname varchar(200), IN pproperty varchar(200), IN pvalue varchar(4000), IN pfromobject varchar(200), IN pdebuginfo varchar(2000))
BEGIN
  DECLARE vDebugTrace varchar(2000) DEFAULT '';
  DECLARE vObjectID int DEFAULT 0;
  DECLARE vObjectCount int DEFAULT 0;
  DECLARE vObjectTypeID int DEFAULT 0;
  DECLARE vObjectTypePropertyID int DEFAULT 0;
  DECLARE vPropertyID int DEFAULT 0;
  DECLARE vPropertyValue varchar(4000);
  DECLARE vPropertyCount int DEFAULT 0;
  DECLARE vObjectTrustCount int DEFAULT 0;
  DECLARE vFromObjectType varchar(255);
  DECLARE vMinTrustLevel int DEFAULT 0;
  DECLARE vOldTrustLevel int DEFAULT 0;
  DECLARE vNewTrustLevel int DEFAULT 50;
  DECLARE vTrustLevelExists int DEFAULT 0;
  DECLARE vEventCount int;
  #This proc runs thousands of times and must use efficient SQL, edit out the use of generic views
  SET vDebugTrace = CONCAT(pdebuginfo, ' -> object_property_set');
  # 049 The following function was improved to not use the v_object view and should save likes of work
  SET vObjectCount = osae_fn_object_exists(pfromobject);
  IF vObjectCount = 1 THEN
    # 049 View replaced with efficient sql below 
    SET vPropertyCount = osae_fn_object_property_exists(pname, pproperty);
    IF vPropertyCount > 0 THEN
      # 049 Below call optomized to replace all views
      SET vTrustLevelExists = osae_fn_trust_level_property_exists(pfromobject);
      SET vObjectID = osae_fn_object_getid(pname);
      SELECT
        object_type_id,
        min_trust_level INTO vObjectTypeID, vMinTrustLevel
      FROM osae_object
      WHERE object_id = vObjectID;
      SELECT
        trust_level,
        object_type_property_id INTO vOldTrustLevel, vObjectTypePropertyID
      FROM osae_v_object_property
      WHERE object_id = vObjectID
      AND property_name = pproperty
      AND (property_value IS NULL
      OR property_value != pvalue);
      SELECT
        property_value INTO vNewTrustLevel
      FROM osae_v_object_property
      WHERE object_name = pfromobject
      AND property_name = 'TRUST LEVEL';
      SELECT
        property_id,
        COALESCE(property_value, '') INTO vPropertyID, vPropertyValue
      FROM osae_v_object_property
      WHERE object_id = vObjectID
      AND property_name = pproperty
      AND (property_value IS NULL
      OR property_value != pvalue);
      #Insert Trust Level Rejection Code Here, maybe shppech command until conversation tracking is in
      IF vNewTrustLevel >= vMinTrustLevel THEN
        UPDATE osae_object_property
        SET property_value = pvalue,
            trust_level = vNewTrustLevel,
            source_name = pfromobject,
            interest_level = 0
        WHERE object_id = vObjectID
        AND object_type_property_id = vPropertyID;
        SELECT
          COUNT(event_id) INTO vEventCount
        FROM osae_object_type_event
        WHERE object_type_id = vObjectTypeID
        AND event_name = pproperty;
        IF vEventCount > 0 THEN
          CALL osae_sp_event_log_add(pname, pproperty, pfromobject, vDebugTrace, pvalue, NULL);
        END IF;
        #Since this property has changed, it has generated interest
        #Review the where clause, don't remember why it has to be null to get this update
        UPDATE osae_object_property
        SET interest_level = interest_level + 1
        WHERE object_type_property_id = vObjectTypePropertyID
        AND (property_value IS NULL
        OR property_value = '');
      ELSE
        CALL osae_sp_debug_log_add(CONCAT(pfromobject, ' tried to set properties on ', pname, ', but lacks sufficient trust'), 'object_property_set');
      END IF;
    END IF;
  END IF;
END
$$

--
-- Create procedure "osae_sp_object_state_set"
--
CREATE DEFINER = 'osae'@'%'
PROCEDURE osae_sp_object_state_set (IN pname varchar(200), IN pstate varchar(50), IN pfromobject varchar(200), IN pdebuginfo varchar(2000))
BEGIN
  DECLARE vObjectCount int;
  DECLARE vObjectID int;
  DECLARE vObjectTypeID int;
  DECLARE vStateCount int;
  DECLARE vOldStateID int;
  DECLARE vStateID int;
  DECLARE vEventCount int;
  DECLARE vHideRedundantEvents int;
  DECLARE vDebugTrace varchar(2000) DEFAULT '';
  SET vDebugTrace = CONCAT(pdebuginfo, ' -> osae_sp_object_state_set');
  SELECT
    COUNT(object_id) INTO vObjectCount
  FROM osae_object
  WHERE UPPER(object_name) = UPPER(pname)
  OR UPPER(object_alias) = UPPER(pname);
  IF vObjectCount = 1 THEN
    SELECT
      object_id,
      object_type_id,
      state_id INTO vObjectID, vObjectTypeID, vOldStateID
    FROM osae_object
    WHERE UPPER(object_name) = UPPER(pname)
    OR UPPER(object_alias) = UPPER(pname) LIMIT 1;
    SELECT
      COUNT(state_id) INTO vStateCount
    FROM osae_object_type_state
    WHERE object_type_id = vObjectTypeID
    AND (UPPER(state_name) = UPPER(pstate)
    OR UPPER(state_label) = UPPER(pstate));
    IF vStateCount = 1 THEN
      SELECT
        state_id INTO vStateID
      FROM osae_object_type_state
      WHERE object_type_id = vObjectTypeID
      AND (UPPER(state_name) = UPPER(pstate)
      OR UPPER(state_label) = UPPER(pstate)) LIMIT 1;
      UPDATE osae_object
      SET state_id = vStateID,
          last_updated = NOW()
      WHERE object_id = vObjectID;
      SELECT
        COUNT(event_id) INTO vEventCount
      FROM osae_v_object_type_event
      WHERE object_type_id = vObjectTypeID
      AND UPPER(event_name) = UPPER(pstate) LIMIT 1;
      IF vEventCount = 1 THEN
        SELECT
          hide_redundant_events INTO vHideRedundantEvents
        FROM osae_v_object_type_event
        WHERE object_type_id = vObjectTypeID
        AND UPPER(event_name) = UPPER(pstate) LIMIT 1;
        IF vOldStateID <> vStateID
          OR vHideRedundantEvents = 0 THEN
          CALL osae_sp_event_log_add(pname, pstate, pfromobject, vDebugTrace, NULL, NULL);
        END IF;
      END IF;
    END IF;
  END IF;
END
$$

