      AND container = 1;
    END IF;
    SELECT
      COUNT(object_id) INTO vAddressCount
    FROM osae_object
    WHERE UPPER(object_name) <> UPPER(poldname)
    AND (UPPER(address) = UPPER(paddress)
    AND address IS NOT NULL
    AND address <> '');
    IF vAddressCount = 0 THEN
      UPDATE osae_object
      SET object_name = pnewname,
          object_alias = vAlias,
          object_description = pdesc,
          object_type_id = vObjectTypeID,
          address = paddress,
          container_id = vContainerID,
          min_trust_level = pmintrustlevel,
          enabled = penabled,
