PROCEDURE osae_sp_schedule_queue_add (IN pscheduleddate datetime, IN pobject varchar(400), IN pmethod varchar(400), IN pparameter1 varchar(2000), IN pparameter2 varchar(2000), IN pscript varchar(200), IN precurringid int(10))
BEGIN
  DECLARE vObjectID int DEFAULT NULL;
  DECLARE vMethodID int DEFAULT NULL;
  DECLARE vScriptID int DEFAULT NULL;
  DECLARE vRecurringID int DEFAULT NULL;
  SELECT
    script_id INTO vScriptID
  FROM osae_script
  WHERE UPPER(script_name) = UPPER(pscript);
  SELECT
    object_id,
    method_id INTO vObjectID, vMethodID
  FROM osae_v_object_method
  WHERE (UPPER(object_name) = UPPER(pobject)
  OR UPPER(object_alias) = UPPER(pobject))
  AND (UPPER(method_name) = UPPER(pmethod)
  OR UPPER(method_label) = UPPER(pmethod));
  IF precurringid > 0 THEN
    SET vRecurringID = precurringid;
  END IF;
  INSERT INTO osae_schedule_queue (queue_datetime, object_id, method_id, parameter_1, parameter_2, script_id, recurring_id)
    VALUES (pscheduleddate, vObjectID, vMethodID, pparameter1, pparameter2, vScriptID, vRecurringID);
END
