--
-- Create procedure "osae_sp_screen_object_position"
--
CREATE DEFINER = 'osae'@'%'
PROCEDURE osae_sp_screen_object_position (IN pobject varchar(255), IN pbyobject varchar(255))
BEGIN
  DECLARE vObject varchar(100);
  DECLARE vBy varchar(100);
  DECLARE vByControlCount int;
  DECLARE vObjectControlCount int;
  DECLARE vByControlID int;
  DECLARE vObjectControlID int;
  DECLARE vNewX int;
  DECLARE vNewY int;
  DECLARE vObjectPropertyXID int;
  DECLARE vObjectPropertyYID int;
  SELECT
    object_name INTO vObject
  FROM osae_v_object
  WHERE UPPER(object_name) = UPPER(pobject)
  OR UPPER(object_alias) = UPPER(pobject);
  SELECT
    object_name INTO vBy
  FROM osae_v_object
  WHERE UPPER(object_name) = UPPER(pbyobject)
  OR UPPER(object_alias) = UPPER(pbyobject);
  SELECT
    COUNT(object_id) INTO vByControlCount
  FROM osae_v_object_property
  WHERE object_type = 'CONTROL STATE IMAGE'
  AND property_name = 'Object Name'
  AND property_value = vBy;
  IF vByControlCount > 0 THEN
    #Get the settings from the screen control to use on the contained control
    SELECT
      object_id INTO vByControlID
    FROM osae_v_object_property
    WHERE object_type = 'CONTROL STATE IMAGE'
    AND property_name = 'Object Name'
    AND property_value = vBy LIMIT 1;
    SELECT
      property_value INTO vNewX
    FROM osae_v_object_property
    WHERE object_id = vByControlID
    AND object_type = 'CONTROL STATE IMAGE'
    AND property_name = 'Contained X';
    SELECT
      property_value INTO vNewY
    FROM osae_v_object_property
    WHERE object_id = vByControlID
    AND object_type = 'CONTROL STATE IMAGE'
    AND property_name = 'Contained Y';
    #The contained object must also have a screen control to bother
    SELECT
      COUNT(object_id) INTO vObjectControlCount
    FROM osae_v_object_property
    WHERE object_type = 'CONTROL STATE IMAGE'
    AND property_name = 'Object Name'
    AND property_value = vObject;
    IF vObjectControlCount > 0 THEN
      SELECT
        object_id INTO vObjectControlID
      FROM osae_v_object_property
      WHERE object_type = 'CONTROL STATE IMAGE'
      AND property_name = 'Object Name'
      AND property_value = vObject LIMIT 1;
      SELECT
        object_property_id INTO vObjectPropertyXID
      FROM osae_v_object_property
      WHERE object_id = vObjectControlID
      AND object_type = 'CONTROL STATE IMAGE'
      AND property_name = 'State 1 X';
      SELECT
        object_property_id INTO vObjectPropertyYID
      FROM osae_v_object_property
      WHERE object_id = vObjectControlID
      AND object_type = 'CONTROL STATE IMAGE'
      AND property_name = 'State 1 Y';
      #So update the contained control to the settings of the container
      UPDATE osae_object_property
      SET property_value = vNewX
      WHERE object_property_id = vObjectPropertyXID;
      UPDATE osae_object_property
      SET property_value = vNewY
      WHERE object_property_id = vObjectPropertyYID;
    END IF;
  END IF;
END
$$

