CREATE
DEFINER = 'osae'@'%'
VIEW osae_v_screen_object
AS
SELECT
  `so`.`screen_object_id` AS `screen_object_id`,
  `so`.`screen_id` AS `screen_id`,
  `so`.`object_id` AS `object_id`,
  `so`.`control_id` AS `control_id`,
  `screen`.`object_name` AS `screen_name`,
  `co`.`enabled` AS `control_enabled`,
  `co`.`object_name` AS `control_name`,
  `oo`.`object_name` AS `object_name`,
  `ot`.`object_type` AS `control_type`,
  `oo`.`last_updated` AS `last_updated`,
  `oo`.`last_state_change` AS `last_state_change`,
  TIMESTAMPDIFF(MINUTE, `oo`.`last_state_change`, NOW()) AS `time_in_state`,
  `ots`.`state_name` AS `state_name`,
  (SELECT
      MAX(`osae_v_object_property`.`last_updated`)
    FROM `osae_v_object_property`
    WHERE ((`osae_v_object_property`.`object_id` = `oo`.`object_id`)
    AND (`osae_v_object_property`.`property_name` <> 'Time'))) AS `property_last_updated`
FROM (((((`osae_screen_object` `so`
  JOIN `osae_object` `screen`
    ON ((`screen`.`object_id` = `so`.`screen_id`)))
  JOIN `osae_object` `oo`
    ON ((`so`.`object_id` = `oo`.`object_id`)))
  JOIN `osae_object` `co`
    ON ((`so`.`control_id` = `co`.`object_id`)))
  JOIN `osae_object_type` `ot`
    ON ((`ot`.`object_type_id` = `co`.`object_type_id`)))
  LEFT JOIN `osae_object_type_state` `ots`
    ON ((`ots`.`state_id` = `oo`.`state_id`)));

