--
-- Create trigger "osae_tr_object_after_insert"
--
CREATE
DEFINER = 'osae'@'%'
TRIGGER osae_tr_object_after_insert
AFTER INSERT
ON osae_object
FOR EACH ROW
BEGIN
  DECLARE vPropertyID int;
  DECLARE vDefault varchar(255);
  DECLARE done int DEFAULT 0;
  DECLARE cur1 CURSOR FOR
  SELECT
    property_id,
    property_default
  FROM osae_object_type_property
  WHERE object_type_id = NEW.`object_type_id`;
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = 1;
  OPEN cur1;
  REPEAT
    FETCH cur1 INTO vPropertyID, vDefault;
    IF NOT done THEN
      INSERT INTO osae_object_property (object_id, object_type_property_id, property_value)
        VALUES (NEW.`object_id`, vPropertyID, vDefault);
    END IF;
  UNTIL done END REPEAT;
  CLOSE cur1;
  INSERT INTO osae_object_state_history (object_id, state_id)
    SELECT
      NEW.`object_id`,
      state_id
    FROM osae_v_object_state
    WHERE object_id = NEW.object_id;
END
$$

--
-- Create trigger "osae_tr_object_after_update"
--
CREATE
DEFINER = 'osae'@'%'
TRIGGER osae_tr_object_after_update
AFTER UPDATE
ON osae_object
FOR EACH ROW
BEGIN
  DECLARE vPersonID int;
  DECLARE vBaseType varchar(200);
  DECLARE vContainerType varchar(200);
  DECLARE vContainerName varchar(100);
  DECLARE vStateName varchar(100);
  DECLARE vDetailedOccupancy varchar(20);
  SELECT
    base_type,
    object_name INTO vContainerType, vContainerName
  FROM osae_v_object
  WHERE object_id = NEW.container_id;
  SELECT
    base_type,
    state_name INTO vBaseType, vStateName
  FROM osae_v_object
  WHERE object_id = OLD.object_id;
  SET vPersonID = (SELECT
      object_type_id
    FROM osae_object_type
    WHERE object_type = 'PERSON');
  IF NEW.object_type_id = vPersonID
    AND OLD.container_id <> NEW.container_id
    AND vContainerType = 'PLACE' THEN
    CALL osae_sp_debug_log_add('Object After Update - Update Screen Position: ', 'SYSTEM');
    #CALL osae_sp_system_count_room_occupants;
    #Update the Screen Objects to reflect the container
    CALL osae_sp_screen_object_position(OLD.object_name, vContainerName);
  END IF;

  SET vDetailedOccupancy = (SELECT
      property_value
    FROM osae_v_object_property
    WHERE object_name = "SYSTEM"
    AND property_name = "Detailed Occupancy Enabled");
  IF vDetailedOccupancy = "TRUE" THEN
    IF OLD.state_id <> NEW.state_id
      AND vBaseType = "SENSOR"
      AND vStateName != "OFF" THEN
      CALL osae_sp_system_who_tripped_sensor(NEW.object_name);
    END IF;
  END IF;
END
$$

