--
-- Create trigger "osae_tr_object_before_update"
--
CREATE
DEFINER = 'osae'@'%'
TRIGGER osae_tr_object_before_update
BEFORE UPDATE
ON osae_object
FOR EACH ROW
BEGIN
  DECLARE vState varchar(50);
  DECLARE vEventCount int;
  DECLARE vHideRedundantRvents int;
  DECLARE vEvent varchar(200);
  DECLARE vSystemID int;
  DECLARE vPlaceID int;
  DECLARE vContainer varchar(200);
  DECLARE vContainerType varchar(200);
  DECLARE vPlaceOnID int;
  DECLARE vPersonID int;
  DECLARE vContainedX int;
  DECLARE vContainedY int;
  IF OLD.object_type_id <> NEW.object_type_id THEN
    DELETE
      FROM osae_object_property
    WHERE object_id = OLD.object_id;
    INSERT INTO osae_object_property (object_id, object_type_property_id, property_value)
      SELECT
        OLD.object_id,
        property_id,
        property_default
      FROM osae_object_type_property
      WHERE object_type_id = NEW.object_type_id;

    DELETE
      FROM osae_object_state_history
    WHERE object_id = OLD.object_id;
    INSERT INTO osae_object_state_history (object_id, state_id)
      SELECT
        OLD.object_id,
        state_id
      FROM osae_object_type_state
      WHERE object_type_id = NEW.object_type_id;

    #DELETE FROM osae_object_property WHERE object_id=OLD.object_id;
    #INSERT INTO osae_object_property (object_id,object_type_property_id,property_value) SELECT OLD.object_id, property_id, property_default FROM osae_object_type_property WHERE object_type_id=NEW.object_type_id;
    DELETE
      FROM osae_object_event_script
    WHERE object_id = OLD.object_id;
  ELSE
    #Changing an Object's Type is so severe, we are only going to check containers and states if the object type is the same
    SET vContainerType = (SELECT
        base_type
      FROM osae_v_object
      WHERE object_id = NEW.container_id);
    SET vPersonID = (SELECT
        object_type_id
      FROM osae_object_type
      WHERE object_type = 'PERSON');
    IF OLD.object_type_id = vPersonID
      AND OLD.container_id <> NEW.container_id
      AND vContainerType = 'PLACE' THEN
      SET vContainer = (SELECT
          object_name
        FROM osae_v_object
        WHERE object_id = NEW.container_id);
      CALL osae_sp_method_queue_add(vContainer, 'ON', '', '', 'SYSTEM', 'Auto-Occupancy Logic');
    END IF;
    IF OLD.state_id <> NEW.state_id THEN
      SET NEW.last_state_change = NOW();
      UPDATE osae_object_state_history
      SET times_this_hour = times_this_hour + 1,
          times_this_day = times_this_day + 1,
          times_this_month = times_this_month + 1,
          times_this_year = times_this_year + 1,
          times_ever = times_ever + 1
      WHERE object_id = OLD.object_id
      AND state_id = NEW.state_id;
      INSERT INTO osae_object_state_change_history (object_id, state_id)
        VALUES (OLD.object_id, NEW.state_id);
    END IF;
  END IF;

  IF OLD.object_name <> NEW.object_name THEN
    UPDATE osae_object_property
    SET property_value = NEW.object_name
    WHERE property_value = OLD.object_name;
  END IF;


END
$$

