--
-- Create trigger "tr_osae_event_log_after_insert"
--
CREATE
DEFINER = 'osae'@'%'
TRIGGER tr_osae_event_log_after_insert
AFTER INSERT
ON osae_event_log
FOR EACH ROW
BEGIN
  DECLARE vEventCount int;
  DECLARE vEventName varchar(200);
  DECLARE vScriptID int;
  DECLARE vObjectTypeID int;
  DECLARE vTypeName varchar(200);
  DECLARE vObjectName varchar(400);
  DECLARE vScriptSeq int;
  DECLARE vPrevScriptSeq int;
  DECLARE scriptProc varchar(200);
  DECLARE vDebugTrace varchar(2000);
  SET vPrevScriptSeq = -1;
  SET vScriptSeq = 0;
  SET vDebugTrace = CONCAT(COALESCE(NEW.debug_trace, ''), ' -> event_log_after_insert');
  SELECT
    object_name INTO vObjectName
  FROM osae_object
  WHERE object_id = NEW.object_id LIMIT 1;
  #CALL osae_sp_debug_log_add(CONCAT('Event_Trigger is running for ',vObjectName),vDebugTrace);

  SELECT
    COUNT(event_script_id) INTO vEventCount
  FROM osae_v_object_event_script
  WHERE object_id = NEW.object_id
  AND event_id = NEW.event_id
  AND script IS NOT NULL
  AND script <> '';
  IF vEventCount > 0 THEN
    SELECT
      event_name INTO vEventName
    FROM osae_v_object_event_script
    WHERE object_id = NEW.object_id
    AND event_id = NEW.event_id;
    CALL osae_sp_debug_log_add(CONCAT('Event_Trigger is running for ', vObjectName, ' ', vEventName), vDebugTrace);
    WHILE vPrevScriptSeq != vScriptSeq DO
      SET vPrevScriptSeq = vScriptSeq;
      SELECT
        script_sequence,
        s.script_id,
        sp.script_processor_plugin_name INTO vScriptSeq, vScriptID, scriptProc
      FROM osae_v_object_event_script e
        INNER JOIN osae_script s
          ON e.script_id = s.script_id
        INNER JOIN osae_script_processors sp
          ON sp.script_processor_id = s.script_processor_id
      WHERE object_id = NEW.object_id
      AND event_id = NEW.event_id
      AND s.script IS NOT NULL
      AND s.script <> ''
      AND script_sequence > vScriptSeq
      ORDER BY script_sequence ASC LIMIT 1;
      IF vPrevScriptSeq != vScriptSeq THEN
        # CALL osae_sp_debug_log_add(CONCAT(vScriptSeq, '-',vScriptID,'-',scriptProc),vDebugTrace);
        CALL osae_sp_method_queue_add(scriptProc, 'RUN SCRIPT', vScriptID, vObjectName, 'SYSTEM', vDebugTrace);
      END IF;
    END WHILE;
  END IF;
  SELECT
    object_type_id INTO vObjectTypeID
  FROM osae_v_object
  WHERE object_id = NEW.object_id;
  SELECT
    COUNT(object_type_event_script_id) INTO vEventCount
  FROM osae_v_object_type_event_script
  WHERE object_type_id = vObjectTypeID
  AND event_id = NEW.event_id
  AND script IS NOT NULL
  AND script <> '';
  IF vEventCount > 0 THEN
    SELECT
      event_name,
      object_type INTO vEventName, vTypeName
    FROM osae_v_object_type_event_script
    WHERE object_type_id = vObjectTypeID
    AND event_id = NEW.event_id;
    CALL osae_sp_debug_log_add(CONCAT('Event_Trigger is running for ', vTypeName, ' ', vEventName), vDebugTrace);
    #CALL osae_sp_debug_log_add(CONCAT('Event_Trigger is running for ',vObjectName,' ',vEventName),vDebugTrace);
    WHILE vPrevScriptSeq != vScriptSeq DO
      SET vPrevScriptSeq = vScriptSeq;
      SELECT
        script_sequence,
        s.script_id,
        sp.script_processor_plugin_name INTO vScriptSeq, vScriptID, scriptProc
      FROM osae_v_object_type_event_script e
        INNER JOIN osae_script s
          ON e.script_id = s.script_id
        INNER JOIN osae_script_processors sp
          ON sp.script_processor_id = s.script_processor_id
      WHERE object_type_id = vObjectTypeID
      AND event_id = NEW.event_id
      AND s.script IS NOT NULL
      AND s.script <> ''
      AND script_sequence > vScriptSeq
      ORDER BY script_sequence ASC LIMIT 1;
      IF vPrevScriptSeq != vScriptSeq THEN
        # CALL osae_sp_debug_log_add(CONCAT(vScriptSeq, '-',vScriptID,'-',scriptProc),vDebugTrace);
        CALL osae_sp_method_queue_add(scriptProc, 'RUN SCRIPT', vScriptID, vObjectName, 'SYSTEM', vDebugTrace);
      END IF;
    END WHILE;
  END IF;
END
$$

